shared/schema.ts.content:
import { sql } from 'drizzle-orm';
import {
  index,
  integer,
  jsonb,
  pgTable,
  serial,
  text,
  timestamp,
  uniqueIndex,
  varchar,
  date,
  boolean,
} from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const updateUserProfileSchema = z.object({
  bio: z.string().max(500).optional(),
  profileImageUrl: z.string().url().optional(),
});
export type UpdateUserProfile = z.infer<typeof updateUserProfileSchema>;

// Session storage table for Replit Auth
export const sessions = pgTable(
  "sessions",
  {
    sid: varchar("sid").primaryKey(),
    sess: jsonb("sess").notNull(),
    expire: timestamp("expire").notNull(),
  },
  (table) => [index("IDX_session_expire").on(table.expire)],
);

// User storage table for Replit Auth
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  email: varchar("email").unique(),
  firstName: varchar("first_name"),
  lastName: varchar("last_name"),
  profileImageUrl: varchar("profile_image_url"),
  bio: text("bio").default(""),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export type UpsertUser = typeof users.$inferInsert;
export type User = typeof users.$inferSelect;

// Badges table - catalog of achievements
export const badges = pgTable("badges", {
  id: serial("id").primaryKey(),
  name: varchar("name", { length: 100 }).notNull(),
  description: text("description").notNull(),
  icon: varchar("icon", { length: 50 }).notNull(),
  color: varchar("color", { length: 50 }).notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export type Badge = typeof badges.$inferSelect;

// User badges - earned by users
export const userBadges = pgTable(
  "user_badges",
  {
    id: serial("id").primaryKey(),
    userId: varchar("user_id").notNull().references(() => users.id),
    badgeId: integer("badge_id").notNull().references(() => badges.id),
    earnedAt: timestamp("earned_at").defaultNow(),
  },
  (table) => [
    uniqueIndex("user_badge_idx").on(table.userId, table.badgeId),
  ]
);

export type UserBadge = typeof userBadges.$inferSelect;
export type UserBadgeWithDetails = UserBadge & { badge: Badge };

// Characters table
export const characters = pgTable("characters", {
  id: serial("id").primaryKey(),
  userId: varchar("user_id").notNull().references(() => users.id),
  name: varchar("name", { length: 255 }).notNull(),
  archetype: varchar("archetype", { length: 100 }).notNull(),
  background: text("background").notNull(),
  personality: text("personality").notNull(),
  motivation: text("motivation").notNull(),
  flaw: text("flaw").notNull(),
  trait: text("trait").notNull(),
  imageUrl: text("image_url"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const insertCharacterSchema = createInsertSchema(characters).omit({
  id: true,
  createdAt: true,
});
export const updateCharacterSchema = createInsertSchema(characters).omit({
  id: true,
  userId: true,
  createdAt: true,
}).partial();
export type InsertCharacter = z.infer<typeof insertCharacterSchema>;
export type UpdateCharacter = z.infer<typeof updateCharacterSchema>;
export type Character = typeof characters.$inferSelect;

// Environments table
export const environments = pgTable("environments", {
  id: serial("id").primaryKey(),
  userId: varchar("user_id").notNull().references(() => users.id),
  name: varchar("name", { length: 255 }).notNull(),
  type: varchar("type", { length: 100 }).notNull(),
  description: text("description").notNull(),
  atmosphere: text("atmosphere").notNull(),
  keyDetails: text("key_details").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const insertEnvironmentSchema = createInsertSchema(environments).omit({
  id: true,
  createdAt: true,
});
export const updateEnvironmentSchema = createInsertSchema(environments).omit({
  id: true,
  userId: true,
  createdAt: true,
}).partial();
export type InsertEnvironment = z.infer<typeof insertEnvironmentSchema>;
export type UpdateEnvironment = z.infer<typeof updateEnvironmentSchema>;
export type Environment = typeof environments.$inferSelect;

// Props table
export const props = pgTable("props", {
  id: serial("id").primaryKey(),
  userId: varchar("user_id").notNull().references(() => users.id),
  name: varchar("name", { length: 255 }).notNull(),
  category: varchar("category", { length: 100 }).notNull(),
  description: text("description").notNull(),
  appearance: text("appearance").notNull(),
  significance: text("significance").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const insertPropSchema = createInsertSchema(props).omit({
  id: true,
  createdAt: true,
});
export const updatePropSchema = createInsertSchema(props).omit({
  id: true,
  userId: true,
  createdAt: true,
}).partial();
export type InsertProp = z.infer<typeof insertPropSchema>;
export type UpdateProp = z.infer<typeof updatePropSchema>;
export type Prop = typeof props.$inferSelect;

// Credit accounts table - stores user credit balance
export const creditAccounts = pgTable("credit_accounts", {
  id: serial("id").primaryKey(),
  userId: varchar("user_id").notNull().references(() => users.id).unique(),
  balance: integer("balance").notNull().default(100),
  totalEarned: integer("total_earned").notNull().default(100),
  totalSpent: integer("total_spent").notNull().default(0),
  lastDailyReward: timestamp("last_daily_reward"),
  loginStreak: integer("login_streak").notNull().default(0),
  createdAt: timestamp("created_at").defaultNow(),
  updatedAt: timestamp("updated_at").defaultNow(),
});

export const insertCreditAccountSchema = createInsertSchema(creditAccounts).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});
export type InsertCreditAccount = z.infer<typeof insertCreditAccountSchema>;
export type CreditAccount = typeof creditAccounts.$inferSelect;

// Credit transactions table - ledger of all credit changes
export const creditTransactions = pgTable("credit_transactions", {
  id: serial("id").primaryKey(),
  userId: varchar("user_id").notNull().references(() => users.id),
  amount: integer("amount").notNull(),
  type: varchar("type", { length: 50 }).notNull(),
  source: varchar("source", { length: 100 }).notNull(),
  description: text("description"),
  balanceAfter: integer("balance_after").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export const insertCreditTransactionSchema = createInsertSchema(creditTransactions).omit({
  id: true,
  createdAt: true,
});
export type InsertCreditTransaction = z.infer<typeof insertCreditTransactionSchema>;
export type CreditTransaction = typeof creditTransactions.$inferSelect;

// Quest templates - catalog of available quests
export const questTemplates = pgTable("quest_templates", {
  id: serial("id").primaryKey(),
  name: varchar("name", { length: 255 }).notNull(),
  description: text("description").notNull(),
  type: varchar("type", { length: 50 }).notNull(),
  requirement: integer("requirement").notNull().default(1),
  rewardCredits: integer("reward_credits").notNull(),
  icon: varchar("icon", { length: 50 }),
  isActive: boolean("is_active").notNull().default(true),
  createdAt: timestamp("created_at").defaultNow(),
});

export const insertQuestTemplateSchema = createInsertSchema(questTemplates).omit({
  id: true,
  createdAt: true,
});
export type InsertQuestTemplate = z.infer<typeof insertQuestTemplateSchema>;
export type QuestTemplate = typeof questTemplates.$inferSelect;

// User daily quests - per-user quest assignments
export const userDailyQuests = pgTable(
  "user_daily_quests",
  {
    id: serial("id").primaryKey(),
    userId: varchar("user_id").notNull().references(() => users.id),
    questTemplateId: integer("quest_template_id").notNull().references(() => questTemplates.id),
    questDate: date("quest_date").notNull(),
    progress: integer("progress").notNull().default(0),
    isCompleted: boolean("is_completed").notNull().default(false),
    isClaimed: boolean("is_claimed").notNull().default(false),
    claimedAt: timestamp("claimed_at"),
    createdAt: timestamp("created_at").defaultNow(),
  },
  (table) => [
    uniqueIndex("user_quest_date_idx").on(table.userId, table.questTemplateId, table.questDate),
  ]
);

export const insertUserDailyQuestSchema = createInsertSchema(userDailyQuests).omit({
  id: true,
  createdAt: true,
  claimedAt: true,
});
export type InsertUserDailyQuest = z.infer<typeof insertUserDailyQuestSchema>;
export type UserDailyQuest = typeof userDailyQuests.$inferSelect;

// Generation sessions table - tracks AI generation time
export const generationSessions = pgTable("generation_sessions", {
  id: serial("id").primaryKey(),
  userId: varchar("user_id").notNull().references(() => users.id),
  type: varchar("type", { length: 50 }).notNull(), // 'character', 'environment', 'prop'
  durationSeconds: integer("duration_seconds").notNull(),
  createdAt: timestamp("created_at").defaultNow(),
});

export type GenerationSession = typeof generationSessions.$inferSelect;

client/src/pages/PropCreatorPage.tsx.content:
import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Plus, Box, Trash2, Save, Copy, BookOpen } from 'lucide-react';
import { Navigation } from '@/components/Navigation';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { toast } from 'sonner';

interface Prop {
  id: string;
  name: string;
  category: string;
  description: string;
  appearance: string;
  significance: string;
  createdAt: number;
}

const PROP_CATEGORIES = [
  'Weapon',
  'Tool',
  'Artifact',
  'Technology',
  'Furniture',
  'Vehicle',
  'Jewelry',
  'Document',
  'Magical Item',
  'Decoration'
];

export default function PropCreatorPage() {
  const [props, setProps] = useState<Prop[]>([]);
  const [selectedProp, setSelectedProp] = useState<Prop | null>(null);
  const [isCreating, setIsCreating] = useState(false);
  const queryClient = useQueryClient();

  const updateQuestProgressMutation = useMutation({
    mutationFn: async (questType: string) => {
      const response = await apiRequest('POST', '/api/quests/progress', {
        questType,
        increment: 1,
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/quests/daily'] });
    },
  });

  const savePropMutation = useMutation({
    mutationFn: async (prop: Prop) => {
      const response = await apiRequest('POST', '/api/props', {
        name: prop.name,
        category: prop.category,
        description: prop.description,
        appearance: prop.appearance,
        significance: prop.significance,
      });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/props'] });
      queryClient.invalidateQueries({ queryKey: ['/api/history'] });
      updateQuestProgressMutation.mutate('prop_create');
      toast.success('Prop saved to your library');
    },
    onError: () => {
      toast.error('Failed to save prop');
    },
  });

  const handleCreateNew = () => {
    const newProp: Prop = {
      id: Math.random().toString(36).substr(2, 9),
      name: 'New Prop',
      category: 'Artifact',
      description: '',
      appearance: '',
      significance: '',
      createdAt: Date.now()
    };
    setProps([newProp, ...props]);
    setSelectedProp(newProp);
    setIsCreating(true);
  };

  const handleUpdate = (field: keyof Prop, value: string) => {
    if (!selectedProp) return;
    const updated = { ...selectedProp, [field]: value };
    setSelectedProp(updated);
    setProps(props.map(p => p.id === updated.id ? updated : p));
  };

  const handleDelete = (id: string) => {
    setProps(props.filter(p => p.id !== id));
    if (selectedProp?.id === id) {
      setSelectedProp(null);
    }
    toast.success('Prop removed');
  };

  const handleSave = () => {
    if (!selectedProp) return;
    savePropMutation.mutate(selectedProp);
    setIsCreating(false);
  };

  const handleCopy = (prop: Prop) => {
    const text = `
Name: ${prop.name}
Category: ${prop.category}
Description: ${prop.description}
Appearance: ${prop.appearance}
Significance: ${prop.significance}
    `.trim();
    navigator.clipboard.writeText(text);
    toast.success('Prop details copied to clipboard');
  };

  return (
    <div className="min-h-screen bg-background text-foreground flex flex-col">
      <Navigation title="Prop Creator" showBackButton={true} backHref="/narrative-studio" showNavButtons={true} />

      <div className="flex-1 overflow-hidden flex">
        {/* Main Editor */}
        <div className="flex-1 flex flex-col items-center justify-center p-8">
          {selectedProp ? (
            <div className="w-full max-w-2xl space-y-6">
              <div className="space-y-4">
                <div>
                  <Label>Prop Name</Label>
                  <Input
                    value={selectedProp.name}
                    onChange={(e) => handleUpdate('name', e.target.value)}
                    className="mt-2 bg-background"
                  />
                </div>

                <div>
                  <Label>Category</Label>
                  <div className="flex flex-wrap gap-2 mt-2">
                    {PROP_CATEGORIES.map(category => (
                      <Badge
                        key={category}
                        variant={selectedProp.category === category ? 'default' : 'outline'}
                        className="cursor-pointer"
                        onClick={() => handleUpdate('category', category)}
                      >
                        {category}
                      </Badge>
                    ))}
                  </div>
                </div>

                <div>
                  <Label>Description</Label>
                  <Textarea
                    value={selectedProp.description}
                    onChange={(e) => handleUpdate('description', e.target.value)}
                    placeholder="What is this prop? What's its purpose?"
                    className="min-h-[120px] font-serif bg-background resize-y mt-2"
                  />
                </div>

                <div>
                  <Label>Appearance & Design</Label>
                  <Textarea
                    value={selectedProp.appearance}
                    onChange={(e) => handleUpdate('appearance', e.target.value)}
                    placeholder="How does it look? Colors, materials, size, distinctive features..."
                    className="min-h-[100px] font-serif bg-background resize-y mt-2"
                  />
                </div>

                <div>
                  <Label>Narrative Significance</Label>
                  <Textarea
                    value={selectedProp.significance}
                    onChange={(e) => handleUpdate('significance', e.target.value)}
                    placeholder="Why is this prop important to the story? What role does it play?"
                    className="min-h-[100px] font-serif bg-background resize-y mt-2"
                  />
                </div>
              </div>

              <div className="flex gap-3 pt-4">
                <Button 
                  onClick={handleSave} 
                  className="flex-1"
                  disabled={savePropMutation.isPending}
                >
                  <Save className="w-4 h-4 mr-2" />
                  {savePropMutation.isPending ? 'Saving...' : 'Save to Library'}
                </Button>
                <Button
                  variant="secondary"
                  onClick={() => handleCopy(selectedProp)}
                  className="flex-1"
                >
                  <Copy className="w-4 h-4 mr-2" />
                  Copy
                </Button>
                <Button
                  variant="outline"
                  onClick={() => handleDelete(selectedProp.id)}
                  className="text-destructive hover:text-destructive"
                >
                  <Trash2 className="w-4 h-4" />
                </Button>
              </div>
            </div>
          ) : (
            <div className="text-center space-y-6">
              <div className="w-20 h-20 rounded-full bg-primary/10 flex items-center justify-center mx-auto">
                <Box className="w-10 h-10 text-primary" />
              </div>
              <div>
                <h2 className="text-3xl font-bold mb-2">Create Props</h2>
                <p className="text-muted-foreground max-w-sm mx-auto">
                  Design objects, weapons, artifacts, and items that shape your narrative world.
                </p>
              </div>
              <Button
                size="lg"
                className="h-12 px-8"
                onClick={handleCreateNew}
              >
                <Plus className="w-5 h-5 mr-2" />
                Create New Prop
              </Button>
            </div>
          )}
        </div>

        {/* History Panel */}
        {props.length > 0 && (
          <>
            <div className="w-80 border-l border-border bg-card/50 backdrop-blur-sm flex flex-col">
              <div className="p-4 border-b border-border/50">
                <h3 className="font-semibold text-sm">Props</h3>
                <p className="text-xs text-muted-foreground mt-1">{props.length} total</p>
              </div>
              <ScrollArea className="flex-1">
                <div className="p-3 space-y-2">
                  {props.map((prop) => (
                    <button
                      key={prop.id}
                      onClick={() => setSelectedProp(prop)}
                      className={`w-full text-left p-3 rounded-lg border transition-all ${
                        selectedProp?.id === prop.id
                          ? 'border-primary bg-primary/10'
                          : 'border-white/5 hover:border-primary/30 hover:bg-white/5'
                      }`}
                    >
                      <div className="font-medium text-sm">{prop.name}</div>
                      <Badge variant="outline" className="mt-1 text-xs">{prop.category}</Badge>
                    </button>
                  ))}
                </div>
              </ScrollArea>
              <div className="p-3 border-t border-border/50">
