  --font-sans: 'Inter', sans-serif;
  --font-serif: 'Lora', serif;
  --font-mono: 'JetBrains Mono', monospace;
}

/* DARK MODE BY DEFAULT (Simulating a creative tool) */
:root {
  --radius: 0.5rem;

  /* Deep Charcoal / Obsidian Theme */
  --background: 224 71% 4%;
  --foreground: 210 40% 98%;

  --card: 222 47% 11%;
  --card-foreground: 210 40% 98%;

  --popover: 222 47% 11%;
