  --sidebar-primary: 262 83% 58%;
  --sidebar-primary-foreground: 210 40% 98%;
  --sidebar-accent: 217 19% 27%;
  --sidebar-accent-foreground: 210 40% 98%;
  --sidebar-border: 217 19% 27%;
  --sidebar-ring: 262 83% 58%;
}

.dark {
  /* Same as root for now since we want dark by default */
  --background: 224 71% 4%;
  --foreground: 210 40% 98%;
  --card: 222 47% 11%;
  --card-foreground: 210 40% 98%;
  --popover: 222 47% 11%;
  --popover-foreground: 210 40% 98%;
  --primary: 262 83% 58%;
  --primary-foreground: 210 40% 98%;
  --secondary: 217 19% 27%;
  --secondary-foreground: 210 40% 98%;
  --muted: 217 19% 27%;
  --muted-foreground: 215 20% 65%;
  --accent: 217 19% 27%;
  --accent-foreground: 210 40% 98%;
  --destructive: 0 62.8% 30.6%;
  --destructive-foreground: 210 40% 98%;
  --border: 217 19% 27%;
  --input: 217 19% 27%;
  --ring: 262 83% 58%;
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply font-sans antialiased bg-background text-foreground;
    /* Force dark mode feel */
    background-color: hsl(var(--background));
    color: hsl(var(--foreground));
  }
}

/* Custom React Flow Styling */
.react-flow__node {
  @apply border-0 rounded-lg shadow-lg;
}

.react-flow__handle {
  @apply w-3 h-3 bg-primary border-2 border-background;
