} from 'lucide-react';
import type { CreditAccount } from '@shared/schema';

interface NavigationProps {
  title: string;
  showBackButton?: boolean;
  backHref?: string;
  showNavButtons?: boolean;
}

export function Navigation({ 
  title, 
